import { useState, useEffect, useCallback } from "react";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Avatar from "@material-ui/core/Avatar";
import Container from "@material-ui/core/Container";
import React from "react";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import { CardActionArea, CardMedia, Grid, Button, CircularProgress } from "@material-ui/core";
import logo from "./logo.PNG";
import image from "./bg.png";
import { DropzoneArea } from 'material-ui-dropzone';
import { common } from '@material-ui/core/colors';
import Clear from '@material-ui/icons/Clear';

const ColorButton = withStyles((theme) => ({
  root: {
    color: theme.palette.getContrastText(common.white),
    backgroundColor: common.white,
    '&:hover': {
      backgroundColor: '#ffffff7a',
    },
  },
}))(Button);

const axios = require("axios").default;

const useStyles = makeStyles((theme) => ({
  grow: {
    flexGrow: 1,
  },
  clearButton: {
    width: "-webkit-fill-available",
    borderRadius: "15px",
    padding: "15px 22px",
    color: "#000000a6",
    fontSize: "20px",
    fontWeight: 900,
  },
  root: {
    maxWidth: 345,
    flexGrow: 1,
  },
  media: {
    height: 400,
  },
  gridContainer: {
    justifyContent: "center",
    padding: "4em 1em 0 1em",
  },
  mainContainer: {
    backgroundImage: `url(${image})`,
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    height: "93vh",
    marginTop: "8px",
  },
  imageCard: {
    margin: "auto",
    maxWidth: 400,
    height: 500,
    backgroundColor: 'transparent',
    boxShadow: '0px 9px 70px 0px rgb(0 0 0 / 30%) !important',
    borderRadius: '15px',
  },
  resultContainer: {
    backgroundColor: "rgba(0, 128, 0, 0.7)", // Transparent green
    color: "white",
    padding: "10px",
    borderRadius: "10px",
    textAlign: "center",
  },
  appbar: {
    background: 'rgba(0, 0, 0, 0.5)', // Transparent black
    boxShadow: 'none',
    color: 'white',
    fontSize: '24px',
    fontWeight: 'bold',
  },
  loader: {
    color: '#be6a77 !important',
  }
}));

export const ImageUpload = () => {
  const classes = useStyles();
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [data, setData] = useState(null);
  const [image, setImage] = useState(false);
  const [isLoading, setIsloading] = useState(false);
  const [confidence, setConfidence] = useState(0);

  const sendFile = useCallback(async () => {
    if (image) {
      let formData = new FormData();
      formData.append("file", selectedFile);
      setIsloading(true);
      try {
        let res = await axios({
          method: "post",
          url: process.env.REACT_APP_API_URL,
          data: formData,
        });
        if (res.status === 200) {
          setData(res.data);
          setConfidence((parseFloat(res.data.confidence) * 100).toFixed(2));
        }
      } catch (error) {
        console.error("Error uploading file:", error);
      }
      setIsloading(false);
    }
  }, [image, selectedFile]);

  const clearData = () => {
    setData(null);
    setImage(false);
    setSelectedFile(null);
    setPreview(null);
    setConfidence(0);
  };

  useEffect(() => {
    if (!selectedFile) {
      setPreview(undefined);
      return;
    }
    const objectUrl = URL.createObjectURL(selectedFile);
    setPreview(objectUrl);
  }, [selectedFile]);

  useEffect(() => {
    if (!preview) {
      return;
    }
    sendFile();
  }, [preview, sendFile]);

  return (
    <React.Fragment>
      <AppBar position="static" className={classes.appbar}>
        <Toolbar>
          <Typography className={classes.title} variant="h6" noWrap>
            EcoDetect
          </Typography>
          <div className={classes.grow} />
          <Avatar src={logo}></Avatar>
        </Toolbar>
      </AppBar>
      <Container maxWidth={false} className={classes.mainContainer} disableGutters={true}>
        <Grid container className={classes.gridContainer} direction="row" justifyContent="center" alignItems="center" spacing={2}>
          <Grid item xs={12}>
            <Card className={`${classes.imageCard} ${!image ? classes.imageCardEmpty : ''}`}>
              {image && <CardActionArea>
                <CardMedia className={classes.media} image={preview} component="img" title="Uploaded Image" />
              </CardActionArea>}
              {!image && <CardContent className={classes.content}>
                <DropzoneArea acceptedFiles={['image/*']} dropzoneText={"Drag and drop an image of a leaf to process"} onChange={setSelectedFile} />
              </CardContent>}
              {isLoading && (
                <CardContent className={classes.resultContainer}>
                  <CircularProgress className={classes.loader} />
                  <Typography variant="h6">Processing...</Typography>
                </CardContent>
              )}
              {data && !isLoading && (
                <CardContent className={classes.resultContainer}>
                  <Typography variant="h6">Detected Class: {data.class}</Typography>
                  <Typography variant="h6">Confidence: {confidence}%</Typography>
                </CardContent>
              )}
            </Card>
          </Grid>
          {data &&
            <Grid item>
              <ColorButton variant="contained" className={classes.clearButton} color="primary" onClick={clearData} startIcon={<Clear fontSize="large" />}>
                Clear
              </ColorButton>
            </Grid>}
        </Grid>
      </Container>
    </React.Fragment>
  );
};
