import { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { CircularProgress } from "@material-ui/core";
import "./index.css";

export const ImageUpload = () => {
  const [selectedFile, setSelectedFile] = useState();
  const [preview, setPreview] = useState();
  const [data, setData] = useState();
  const [image, setImage] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const sendFile = useCallback(async () => {
    if (image) {
      let formData = new FormData();
      formData.append("file", selectedFile);
      try {
        let res = await axios.post(process.env.REACT_APP_API_URL, formData);
        if (res.status === 200) {
          setData(res.data);
        }
      } catch (error) {
        console.error("Error sending file:", error);
      }
      setIsLoading(false);
    }
  }, [image, selectedFile]);

  const clearData = () => {
    setData(null);
    setImage(false);
    setSelectedFile(null);
    setPreview(null);
  };

  useEffect(() => {
    if (!selectedFile) {
      setPreview(undefined);
      return;
    }
    const objectUrl = URL.createObjectURL(selectedFile);
    setPreview(objectUrl);
  }, [selectedFile]);

  useEffect(() => {
    if (!preview) return;
    setIsLoading(true);
    sendFile();
  }, [preview, sendFile]);

  const onSelectFile = (files) => {
    if (!files || files.length === 0) {
      setSelectedFile(undefined);
      setImage(false);
      setData(undefined);
      return;
    }
    setSelectedFile(files[0]);
    setData(undefined);
    setImage(true);
  };

  return (
    <div className="container">
      <header className="app-header">
        <h1 className="app-title">EcoDetect</h1>
        <img src="/logo.PNG" alt="Logo" className="app-logo" />
      </header>

      <div className="upload-container">
        <input type="file" onChange={(e) => onSelectFile(e.target.files)} />
        {isLoading && <CircularProgress className="loader" />}
      </div>

      {preview && <img src={preview} alt="Preview" className="preview-image" />}

      {data && (
        <div className="result-box">
          <p>Detected Class: {data.class}</p>
          <p>Confidence: {parseFloat(data.confidence) * 100}%</p>
        </div>
      )}

      <button className="clear-button" onClick={clearData}>Clear</button>
    </div>
  );
};
